function getWaterTemperature(data) {
    if (data === -127) {
        return context.get('waterTemperature')
    } else {
        context.set('waterTemperature', data)

        return data
    }
}

/** 
 * Fungsi untuk menghitung nilai TDS
 * 
 * @param {number} data
 * @param {number} temperature
*/
function getTds(data, temperature) {
    let tds;

    if (flow.get('nutrientPumpStatus') == "0") {
        tds = flow.get("tds")
    } else {
        const kValue = 0.79;
        const aref = 3.3;
        const adcRange = 1024;  //setting according to Gravity TDS Master library
        const tdsFactor = 0.5;
        let voltage = data / adcRange * aref;
        let ecValue = (133.42 * voltage * voltage * voltage - 255.86 * voltage * voltage + 857.39 * voltage) * kValue;
        let ec = ecValue * (1.0 + 0.02 * (temperature - 25));
        tds = ec * tdsFactor;

        tds = Math.round(tds)

        flow.set("tds", tds)
    }

    return tds;
}

/**
 * Fungsi untuk menghitung nilai PH
 * 
 * @param {number} data
 * @return {number} ph
 */
function getPh(data) {
    // let phValue = (0.1111 * data) + 1.000
    let phValue = (-0.01947 * data) + 7.01

    return Number(phValue.toFixed(2))
}

function savetoDatabase(data) {
    Object.assign(data, {
        "minTds": flow.get("minTDS"),
        "maxTds": flow.get("maxTDS")
    })

    return data
}

