// fungsi untuk mengatur kapan pompa cairan harus diaktifkan dan dinonaktifkan
function nutrientPump(airTemp, humidity) {
    let newDate = new Date();
    // @ts-ignore
    let diffMinute = (newDate - context.get('setDate')) / (1000 * 60);
    if (diffMinute < 6) {
        return 0;
    }

    if (airTemp > 28 && humidity < 65) {
        if (diffMinute >= 6 && diffMinute <= 15) {
            return 1;
        }

        if (diffMinute > 15) {
            context.set('setDate', new Date())
            return 0;
        }
    } else if (airTemp < 25 && humidity > 78) {
        if (diffMinute >= 6 && diffMinute <= 45) {
            return 1;
        }

        if (diffMinute > 45) {
            context.set('setDate', new Date())
            return 0;
        }
    } else {
        if (diffMinute >= 6 && diffMinute <= 30) {
            return 1;
        }

        if (diffMinute > 30) {
            context.set('setDate', new Date())
            return 0;
        }
