/**
* @param {number} tdsValue
* @param {number} maxTDS
* @param {number} minTDS
*/
// fungsi untuk mengatur kapan pompa pupuk harus diaktifkan atau di non aktifkan
function fertilizerPump(tdsValue, maxTDS, minTDS) {
    let status, q1, q3;

    q1 = minTDS + ((maxTDS - minTDS) * 0.25)
    q3 = minTDS + ((maxTDS - minTDS) * 0.75)

    if (tdsValue < q1) {
        status = "0"
    } 

    if (tdsValue >= q3) {
        status = "1"
    }

    flow.set("fertilizerPumpStatus", status)

    return status
}

function nutrientPump(predict) {
    let status;

    let dateNow = moment().format("YYYY-MM-DD HH:mm:00")

    // Modifikasi format datetime di array agar sesuai dengan format target
    const match = predict.find(record => {
        // Ambil substring datetime dari array hingga detik diubah menjadi "00"
        const formattedDatetime = record.datetime.substring(0, 17) + "00";
        return formattedDatetime === dateNow;
    });

    if (match) {
        status = match.is_pump_not_active.toString()
    } else {
        status = "1"
    }

    flow.set("nutrientPumpStatus", status)

    return status
}

